import {useState,useEffect} from 'react'
import {showP} from '../api/api'
const Productlist = () => {
    const [product,setProduct] = useState([])
    
    useEffect(()=>{
        show()
    },[])

    const show = async() =>{
        try{
            const res =  await showP()
            setProduct(res.data)
            console.log(product)
        }catch(error){
            console.log(error)
        }
    }

    const handeldel = (id) =>{
        console.log(id)
        try{
            const res = delP()
        }catch(error){
            console.log(error)
        }
    }
   
  return (
    <div className='main'>
        <h1>ProductList</h1>
        <table>
            <td> 
                <th>Name</th>
                <th>Price</th>
                <th>Category</th>
                <th>Company</th>
                <th>Action</th>
                <tbody>
                    {
                        product.map((d,i)=>{
                            const {name,price,category,company,_id} = d;
                            return(
                                <tr>
                                    <td>{name}</td>
                                    <td>{price}</td>
                                    <td>{category}</td>
                                    <td>{company}</td>
                                    <td>
                                        <button>Edit</button>
                                        <button onClick={()=>handeldel(_id)}>Delete</button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </td>
        </table>
    </div>
  )
}

export default Productlist
